import React, {Component} from 'react';

import {Button, Form} from 'react-bootstrap';

export default class InformationForm extends Component {

    constructor(props) {
        super(props);
        this.state = {
            validated: false,
            name: '',
            addr1: '',
            addr2: '',
            city: '',
            state: '',
            zip: '',
            email: '',
            phone: '+1 ',
            contactAllowed: true,

            nameValid: false,
            addr1Valid: false,
            addr2Valid: true,
            cityValid: false,
            stateValid: false,
            zipValid: false,
            emailValid: false,
            phoneValid: false,

            formChanged: false,
            formValid: false
        };

        this.submit = this.submit.bind(this);
        this.handleName = this.handleName.bind(this);
        this.handleAddr1 = this.handleAddr1.bind(this);
        this.handleAddr2 = this.handleAddr2.bind(this);
        this.handleCity = this.handleCity.bind(this);
        this.handleState = this.handleState.bind(this);
        this.handleZip = this.handleZip.bind(this);
        this.handleEmail = this.handleEmail.bind(this);
        this.handlePhone = this.handlePhone.bind(this);
        this.handleCheck = this.handleCheck.bind(this);
        this.validateGeneric = this.validateGeneric.bind(this);
        this.validateState = this.validateState.bind(this);
        this.validateZip = this.validateZip.bind(this);
        this.validateEmail = this.validateEmail.bind(this);
        this.validatePhone = this.validatePhone.bind(this);

        this.stateForInput = this.stateForInput.bind(this);
        this.fullToAbbr = this.fullToAbbr.bind(this);
    }

    submit(event) {
        event.preventDefault();
        event.stopPropagation();
        this.props.nextDisabled(!event.currentTarget.checkValidity());
        if (!(this.state.validated)) {
            this.setState({validated: true, });
        }
        console.log(this.state);
    }

    updateTabs() {
        if(this.state.formChanged) {
            this.state.formValid ? this.props.enablePayment() : this.props.disablePayment();
        }
    }

    handleName(name) {
        if (this.state.name !== name) {
            this.setState({formChanged: true, formValid: this.formValid(), name: name.target.value, nameValid: this.validateGeneric(name.target.value)});
        }
    }

    handleAddr1(addr1) {
        if (this.state.addr1 !== addr1) {
            this.setState({formChanged: true, formValid: this.formValid(), addr1: addr1.target.value, addr1Valid: this.validateGeneric(addr1.target.value)});
        }
    }

    handleAddr2(addr2) {
        if (this.state.addr2 !== addr2) {
            this.setState({formChanged: true, formValid: this.formValid(), addr2: addr2.target.value});
        }
    }

    handleCity(city) {
        if (this.state.city !== city) {
            if (!(/\d/.test(city.target.value))) {
                this.setState({formChanged: true, formValid: this.formValid(), city: city.target.value, cityValid: this.validateGeneric(city.target.value)});
            }
        }
    }

    handleState(state) {
        if (this.state.state !== state) {
            if (!(/\d/.test(state.target.value))) {
                if (state.target.value.length < 24) {
                    this.setState({formChanged: true, formValid: this.formValid(), state: state.target.value, stateValid: this.validateState(state.target.value)});
                }
            }
        }
    }

    handleZip(zip) {
        if (this.state.zip !== zip) {
            if (zip.target.value.length < 6) {
                if (!isNaN(zip.target.value)) {
                    this.setState({formChanged: true, formValid: this.formValid(), zip: zip.target.value, zipValid: this.validateZip(zip.target.value)});
                }
            }
        }
    }

    handleEmail(email) {
        if (this.state.email !== email) {
            this.setState({formChanged: true, formValid: this.formValid(), email: email.target.value, emailValid: this.validateEmail(email.target.value)});
        }
    }

    handlePhone(phone) {
        if (this.state.phone !== phone) {
            let value = phone.target.value;
            if (value.length > 2 && value.length < 20) {
                this.setState({formChanged: true, formValid: this.formValid(), phone: value, phoneValid: this.validatePhone(phone.target.value)});
            }
        }
    }

    handleCheck(contactAllowed) {
        if (this.state.contactAllowed !== contactAllowed) {
            this.setState({contactAllowed: contactAllowed.target.value});
        }
    }

    validateGeneric(generic) {
        let valid = generic.length > 0 && isNaN(generic);
        return valid;
    }

    validateState(state) {
        let valid = !(this.stateForInput(state) == null);
        return valid;
    }

    validateZip(zip) {
        let valid = zip.length === 5 && !isNaN(zip);
        return valid;
    }

    validateEmail(email) {
        var emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        let valid = emailRegex.test(email.toLowerCase());
        return valid;
    }

    validatePhone(phone) {
        let valid = phone.length === 19;
        return valid;
    }

