import React, {Component} from 'react';
import {injectStripe} from 'react-stripe-elements';
import {Form} from 'react-bootstrap';

export default injectStripe(CheckoutForm);

class CheckoutForm extends Component {

    constructor(props) {
        super(props)
        this.submit = this.submit.bind(this)
    }

    async submit(ev) {
        ev.preventDefault()
    }

    render() {
        return (
            <Form>
                <Form.Group controlId="name">
                    <Form.Label>Name / Organization</Form.Label>
                    <Form.Control required type="text" placeholder="John Doe"></Form.Control>
                </Form.Group>
                <Form.Group controlId="email">
                    <Form.Label>Email Address</Form.Label>
                    <Form.Control required type="email" placeholder="example@domain.ext"/>
                    <Form.Text className="text-muted">We'll never share your information with any other entities</Form.Text>
                </Form.Group>
                <Form.Group controlId="contactAllowed">
                    <Form.Check type="checkbox" checked="true" label="Receive updates from donation recipients throughout the season"/>
                </Form.Group>
            </Form>
        );
    }
}
