import React, { Component } from 'react';
import {injectStripe} from 'react-stripe-elements';
import {Tabs, Tab} from 'react-bootstrap';
import InformationForm from './InformationForm';

class CheckoutForm extends Component {

  constructor(props) {
    super(props);
    this.state = {
      paymentEnabled: false,
      confirmationEnabled: false
    }
    this.setPaymentEnabled = this.setPaymentEnabled.bind(this);
  }

  setPaymentEnabled(enabled) {
    this.setState({paymentEnabled : enabled});
  }

  render() {
    return (
      <Tabs>
        <Tab eventKey="information" title="Contact Information">
          <InformationForm enableNext={this.setPaymentEnabled}/>
        </Tab>
        <Tab eventKey="payment" title="Payment" disabled={this.state.paymentEnabled}>

        </Tab>
        <Tab eventKey="confirmation" title="Confirmation" disabled={this.state.confirmationEnabled}>

        </Tab>
      </Tabs>
    );
  }
}

export default injectStripe(CheckoutForm);
