import React, {Component} from 'react';
import {Button, Form} from 'react-bootstrap';

export default class InformationForm extends Component {
    constructor(props) {
        super(props);
        this.state = {validated : false};
        this.submit = this.submit.bind(this);
        this.handleInput = this.handleInput.bind(this);
        this.handlePhone = this.handlePhone.bind(this);
    }

    submit(event) {
        console.log("SUBMIT");
        if (!event.currentTarget.checkValidity()) {
            console.log("INSIDE IF");
            event.preventDefault();
            console.log("POST PREVENT DEFAULT");
            event.stopPropagation();
            console.log("POST STOP PROP");
        }
        console.log("OUTSIDE IF");
    }

    handleInput(event) {
        this.setState({[event.target.name] : event.target.value});
    }

    handlePhone(event) {

    }

    render() {
        return (
            <Form noValidate validated={this.state.validated} onSubmit={e => this.submit(e)}>
                <Form.Row>
                    <Form.Group controlId="name">
                        <Form.Label>Full Name / Organization</Form.Label>
                        <Form.Control required type="text" placeholder="John Doe" />
                        <Form.Control.Feedback type="invalid">
                            Please enter a valid name / organization.
                        </Form.Control.Feedback>
                    </Form.Group>
                </Form.Row>
                <Form.Row>
                    <Form.Group controlId="addr1">
                        <Form.Label>Address Line 1</Form.Label>
                        <Form.Control
                            required
                            type="text"
                            placeholder="12345 Artificial Dr"
                        />
                        <Form.Control.Feedback type="invalid">
                            Please enter a valid address.
                        </Form.Control.Feedback>
                    </Form.Group>
                    &nbsp;
                    <Form.Group controlId="addr2">
                        <Form.Label>Address Line 2</Form.Label>
                        <Form.Control type="text" placeholder="Apt. 27" />
                    </Form.Group>
                </Form.Row>
                <Form.Row>
                    <Form.Group controlId="city">
                        <Form.Label>City</Form.Label>
                        <Form.Control required type="text" placeholder="City" />
                        <Form.Control.Feedback type="invalid">
                            Please enter a valid city.
                        </Form.Control.Feedback>
                    </Form.Group>
                    &nbsp;
                    <Form.Group controlId="state">
                        <Form.Label>State</Form.Label>
                        <Form.Control required type="text" placeholder="State" />
                        <Form.Control.Feedback type="invalid">
                            Please enter a valid state.
                        </Form.Control.Feedback>
                    </Form.Group>
                    &nbsp;
                    <Form.Group controlId="zip">
                        <Form.Label>Zip / Postal Code</Form.Label>
                        <Form.Control required type="text" placeholder="12345" />
                        <Form.Control.Feedback type="invalid">
                            Please enter a valid zip / postal code.
                        </Form.Control.Feedback>
                    </Form.Group>
                </Form.Row>
                <Form.Row>
                    <Form.Group controlId="email">
                        <Form.Label>Email Address</Form.Label>
                        <Form.Control
                            required
                            type="email"
                            placeholder="example@domain.ext"
                        />
                        <Form.Control.Feedback type="invalid">
                            Please enter a valid email.
                        </Form.Control.Feedback>
                    </Form.Group>
                    &nbsp;
                    <Form.Group controlId="phone">
                        <Form.Label>Phone Number</Form.Label>
                        <Form.Control
                            required
                            type="tel"
                            placeholder="+1 (123) 456 - 7890"
                            onChange={e => this.handlePhone(e)}
                        />
                    </Form.Group>
                </Form.Row>
                <Form.Row>
                    <Form.Group controlId="contactAllowed">
                        <Form.Check
                            type="checkbox"
                            defaultChecked="true"
                            label="Receive updates from donation recipients throughout the season"
                        />
                    </Form.Group>
                </Form.Row>
                <Form.Row>
                    <Button type="submit">Submit</Button>
                </Form.Row>
            </Form>
        );
    }
}
