      nextState.addr2Valid,
      nextState.cityValid,
      nextState.stateValid,
      nextState.zipValid,
      nextState.emailValid,
      nextState.phoneValid
    ];
    let thisStateImportant = [
      this.state.validated,
      this.state.name,
      this.state.addr1,
      this.state.addr2,
      this.state.city,
      this.state.state,
      this.state.zip,
