import React, {Component} from 'react';
import {Button, Form} from 'react-bootstrap';

export default class InformationForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            validated: false,
            name: '',
            addr1: '',
            addr2: '',
            city: '',
            state: '',
            zip: '',
            email: '',
            phone: '',
            contactAllowed: true
        }
        this.handleName = this.handleName.bind(this);
        this.handleAddr1 = this.handleAddr1.bind(this);
        this.handleAddr2 = this.handleAddr2.bind(this);
        this.handleCity = this.handleCity.bind(this);
        this.handleState = this.handleZip.bind(this);
        this.handleZip = this.handleZip.bind(this);
        this.handleEmail = this.handleEmail.bind(this);
        this.handlePhone = this.handlePhone.bind(this);
        this.submit = this.submit.bind(this);
        this.validateGeneric = this.validateGeneric.bind(this);
        this.validateZip = this.validateZip.bind(this);
        this.validateEmail = this.validateEmail.bind(this);
        this.validatePhone = this.validatePhone.bind(this);
    }

    submit(event) {
        event.preventDefault();
        event.stopPropagation();
        this.props.nextDisabled(!event.currentTarget.checkValidity());
        this.setState({validated: true});
    }

    handleName(name) {
        this.setState({name: name.target.value});
    }

    handleAddr1(addr1) {
        this.setState({addr1: addr1.target.value});
    }

    handleAddr2(addr2) {
        this.setState({addr2: addr2.target.value});
    }

    handleCity(city) {
        this.setState({city: city.target.value});
    }

    handleState(event) {
        this.setState({state: event.target.value});
    }

    handleZip(event) {
        if (event.target.value.length < 6) {
            if (!isNaN(event.target.value)) {
                this.setState({zip: event.target.value});
            }
        }
    }

    handleEmail(email) {
        this.setState({email: email.target.value});
    }

    handlePhone(phone) {
        this.setState({phone: phone.target.value});
    }

    handleCheck(check) {
        this.setState({checked: check.target.value});
    }

    validateGeneric(generic) {
        return generic.length > 0 && isNaN(generic)
    }

    validateZip(zip) {
        return zip.length === 5 && !isNaN(zip)
    }

    validateEmail(email) {
        var emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
        return emailRegex.test(email.toLowerCase());
    }

    validatePhone(phone) {
        return phone.length === 19;
    }

    render() {
        return (
            <Form noValidate validated={this.state.validated} onSubmit={e => {
                e.target.name = "name";
                this.submit(e);
            }}>
                <Form.Row>
                    <Form.Group controlId="name">
                        <Form.Label>Full Name / Organization</Form.Label>
                        <Form.Control
                            required
                            type="text"
                            placeholder="John Doe"
                            value={this.state.name}
                            onChange={e => this.handleName(e)}
                        />
                        <Form.Control.Feedback type="invalid">
                            Please enter a valid name / organization.
                        </Form.Control.Feedback>
                    </Form.Group>
                </Form.Row>
                <Form.Row>
                    <Form.Group controlId="addr1">
                        <Form.Label>Address Line 1</Form.Label>
                        <Form.Control
                            required
                            type="text"
                            placeholder="12345 Artificial Dr"
                            value={this.state.addr1}
                            onChange={e => this.handleAddr1(e)}
                        />
                        <Form.Control.Feedback type="invalid">
                            Please enter a valid address.
                        </Form.Control.Feedback>
                    </Form.Group>
                    &nbsp;
                    <Form.Group controlId="addr2">
                        <Form.Label>Address Line 2</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Apt. 27"
                            value={this.state.addr2}
                            onChange={e => this.handleAddr2(e)}
                        />
                    </Form.Group>
                </Form.Row>
                <Form.Row>
                    <Form.Group controlId="city">
                        <Form.Label>City</Form.Label>
                        <Form.Control
                            required
                            type="text"
                            placeholder="City"
                            value={this.state.city}
                            onChange={e => this.handleCity(e)}
                        />
                        <Form.Control.Feedback type="invalid">
                            Please enter a valid city.
                        </Form.Control.Feedback>
                    </Form.Group>
                    &nbsp;
                    <Form.Group controlId="state">
                        <Form.Label>State</Form.Label>
                        <Form.Control
                            required
                            type="text"
                            placeholder="State"
                            value={this.state.state}
                            onChange={e => this.handleState(e)}
                        />
                        <Form.Control.Feedback type="invalid">
                            Please enter a valid state.
                        </Form.Control.Feedback>
                    </Form.Group>
                    &nbsp;
                    <Form.Group controlId="zip">
                        <Form.Label>Zip / Postal Code</Form.Label>
                        <Form.Control
                            required
                            type="text"
                            placeholder="12345"
                            value={this.state.zip}
                            onChange={e => this.handleZip(e)}
                        />
                        <Form.Control.Feedback type="invalid">
                            Please enter a valid zip / postal code.
                        </Form.Control.Feedback>
                    </Form.Group>
                </Form.Row>
                <Form.Row>
                    <Form.Group controlId="email">
                        <Form.Label>Email Address</Form.Label>
                        <Form.Control
                            required
                            type="email"
                            placeholder="example@domain.ext"
                            value={this.state.email}
                            onChange={e => this.handleEmail(e)}
                        />
                        <Form.Control.Feedback type="invalid">
                            Please enter a valid email.
                        </Form.Control.Feedback>
                    </Form.Group>
                    &nbsp;
                    <Form.Group controlId="phone">
                        <Form.Label>Phone Number</Form.Label>
                        <Form.Control
                            required
                            type="text"
                            placeholder="+1 (123) 456 - 7890"
                            value={this.state.email}
                            onChange={e => this.handlePhone(e)}
                        />
                    </Form.Group>
                </Form.Row>
                <Form.Row>
                    <Form.Group controlId="contactAllowed">
                        <Form.Check
                            type="checkbox"
                            label="Receive updates from donation recipients throughout the season"
                            checked={this.state.contactAllowed}
                            onChange={e => this.handleCheck(e)}
                        />
                    </Form.Group>
                </Form.Row>
                <Form.Row>
                    <Button type="submit">Submit</Button>
                </Form.Row>
            </Form>
        );
    }
}
